import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.css';


import './Page.css';
import './car.svg';
import '../../car.svg';


function Page() {
  let [state, setState] = useState();

  const handleCarClick = () =>{
    let password = '';
    if (localStorage.getItem('password')){
      password = localStorage.getItem('password')
    }else{
      password = prompt('Введите пароль:');
      if(password != null) {
        localStorage.setItem('password', password);
      }
    };

    fetch('https://mine-doors.herokuapp.com/mobile/car', {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Basic ${password}`
      })
    })
      .then(response => {
        if (response.status === 200){
          setState('OK')
        } else {
          setState('Something went wrong')
          localStorage.clear();
        }
      })
      .catch(e => setState("Что-то не так с сервером или нет интернета")
      )

  }
  console.log(state);


  return (
    <div className="page">
      {state && <div className={`alert blue alert-warning`}>{state}</div>}
      <button className="btn btn-dark" onClick={()=>handleCarClick()}><img src="car.svg"/></button>
    </div>
  );
}

export default Page;
