import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.css';


import './App.css';


function App() {
  let [state, setState] = useState();
  const handleClick = () =>{
    fetch('https://swapi.dev/api/people/', {
      method: 'GET',
      // headers: new Headers({
      //   'Authorization': 'Basic YWxhZGRpbjpvcGVuc2VzYW1l'
      // })
    })
      .then(response => {
        if (response.status === 200){
          setState('OK')
        } else {
          setState('Something went wrong')
        }
      })
      .catch(e => setState("Что-то не так с сервером или нет интернета")
      )

  }
  console.log(state);
  return (
    <div className="App">
      <button className="btn btn-dark" onClick={()=>handleClick()}>OK</button>
      {state && <div className="alert alert-danger blue">{state}</div>}
    </div>
  );
}

export default App;
