import React, {useState} from 'react';

import './App.css';


function App() {
  let [state, setState] = useState();
  const handleClick = () =>{
    fetch('https://mine-doors.herokuapp.com/mobile', {
      method: 'GET',
      headers: new Headers({
        'Authorization': 'Basic YWxhZGRpbjpvcGVuc2VzYW1l'
      })
    })
      .then(response => {
        if (response.status === 200){
          setState('OK')
        } else {
          setState('Something went wrong')
        }
      })

  }
  console.log(state);
  return (
    <div className="App">
      <button className="btn" onClick={()=>handleClick()}>OK</button>
      <div>{state}</div>
    </div>
  );
}

export default App;
